import React, { Component } from 'react'
import { Table, Divider, Tag, Button } from 'antd';
// import personalCenter from '../personalCenter/personalCenter';
import { Card } from 'antd'
import './myStudent.css'
import ajax from '../../api/ajax'

export class myStudent extends Component {
  state = {
    // 点击展示详情显示模块
    showDetails: false,
    columns: [
      {
        title: '姓名',
        dataIndex: 'name',
        key: 'name',
        align: 'center'
      },
      {
        title: '学号',
        dataIndex: 'number',
        key: 'number',
        align: 'center'
      },
      {
        title: '题目',
        dataIndex: 'item',
        key: 'item',
        align: 'center'
      },
      {
        title: '期中成绩',
        dataIndex: 'midterm',
        key: 'midterm',
        align: 'center'
      },
      {
        title: '期末成绩',
        dataIndex: 'final',
        key: 'final',
        align: 'center'
      },
      {
        title: '期末成绩',
        dataIndex: 'score',
        key: 'score',
        align: 'center'
      },
      {
        title: '详情信息',
        key: 'action',
        align: 'center',
        render: (text, record) => (
          <span>
            <Button type="primary" onClick={this.showItem}>详情</Button>
            {/* {this.state.showDetails ? <div>
              <span>{record.name}</span>
              <span>{record.number}</span>
            </div> : null} */}
          </span>
        ),
      },
    ],
    data: [
      {
        key: '0',
        name: 'John',
        number: 16406050305,
        item: '基于ARM嵌入式技术实现的智能小车',
        midterm: 90,
        final: 90,
        score: 95,
        tags: '',
      },
      {
        key: '1',
        name: 'John',
        number: 16406050305,
        item: '高数',
        midterm: 90,
        final: 90,
        score: 95,
        tags: '',
      },
      {
        key: '2',
        name: 'John',
        number: 16406050305,
        item: '高数',
        midterm: 90,
        final: 90,
        score: 95,
        tags: '',
      },
      {
        key: '3',
        name: 'John',
        number: 16406050305,
        item: '高数',
        midterm: 90,
        final: 90,
        score: 95,
        tags: '',
      },
    ]
  }
  showItem = (e, text, record) => {
    e.preventDefault();
    // this.setState({ showDetails: true });
    this.setState(prevState => ({
      showDetails: !prevState.showDetails
    }));
  }

  componentDidMount = () => {
    // const reqResult = () => ajax('47.94.103.140/gdom/lecture/imine');
    // reqResult().then(res => {
    //   console.log(res);
    // }).catch(err => {
    //   console.log(err);
    // })
    console.log(this.props.location.query.id);
    // console.log(this.props.location.query.name);

  }

  click = (recordr, owkey) => {
    // console.log(recordr, owkey)
    // this.setState(prevState => ({
    //   showDetails: !prevState.showDetails
    // }));
  }
  render() {
    const columns = this.state.columns;
    const data = this.state.data;
    const showDetails = this.state.showDetails;
    return (
      <div>
        <Table columns={columns} dataSource={data}
          onRow={(record, rowkey) => {
            return {
              //点击行 record 指的本行的数据内容，rowkey指的是本行的索引
              onClick: this.click.bind(this, record, rowkey)
            }
          }}
        />
        {showDetails ? <Card title="详细信息" style={{ width: 230 }}>
          <p>姓名：</p>
          <p>学号：</p>
          <p>学校：</p>
          <p>专业：</p>
          <p>班级：</p>
          <p>期中成绩：</p>
          <p>期末成绩：</p>
        </Card> : null}
      </div>
    )
  }
}

export default myStudent;
