import React, { Component } from 'react'
import { Table, Input, Button, Icon } from 'antd';
import Highlighter from 'react-highlight-words';

class teacherQuery extends Component {
  state = {
    data: [
      {
        key: '1',
        index: '1',
        name: '杨卓卓',
        cno: '16406050305',
        sex: '女',
        role: '老师',
        school: '西安科技大学',
        collage: '电控学院',
        professional: '自动化',
      },
      {
        key: '2',
        index: '2',
        name: '杨卓',
        cno: '16406050305',
        sex: '女',
        role: '老师',
        school: '西安科技大学',
        collage: '电控学院',
        professional: '自动化',
      },
      {
        key: '3',
        index: '3',
        name: '杨卓',
        cno: '16406050305',
        sex: '女',
        role: '老师',
        school: '西安科技大学',
        collage: '电控学院',
        professional: '自动化',
      },
      {
        key: '4',
        index: '4',
        name: '杨卓',
        cno: '16406050305',
        sex: '女',
        role: '老师',
        school: '西安科技大学',
        collage: '电控学院',
        professional: '自动化',
      },
      {
        key: '5',
        index: '5',
        name: '杨卓',
        cno: '16406050305',
        sex: '女',
        role: '老师',
        school: '西安科技大学',
        collage: '电控学院',
        professional: '自动化',
      },
    ],
    searchText: '',
  };

  getColumnSearchProps = dataIndex => ({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={node => {
            this.searchInput = node;
          }}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={e => setSelectedKeys(e.target.value ? [e.target.value] : [])}
          onPressEnter={() => this.handleSearch(selectedKeys, confirm)}
          style={{ width: 188, marginBottom: 8, display: 'block' }}
        />
        <Button
          type="primary"
          onClick={() => this.handleSearch(selectedKeys, confirm)}
          icon="search"
          size="small"
          style={{ width: 90, marginRight: 8 }}
        >
          Search
        </Button>
        <Button onClick={() => this.handleReset(clearFilters)} size="small" style={{ width: 90 }}>
          Reset
        </Button>
      </div>
    ),
    filterIcon: filtered => (
      <Icon type="search" style={{ color: filtered ? '#1890ff' : undefined }} />
    ),
    onFilter: (value, record) =>
      record[dataIndex]
        .toString()
        .toLowerCase()
        .includes(value.toLowerCase()),
    onFilterDropdownVisibleChange: visible => {
      if (visible) {
        setTimeout(() => this.searchInput.select());
      }
    },
    render: text => (
      <Highlighter
        highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
        searchWords={[this.state.searchText]}
        autoEscape
        textToHighlight={text.toString()}
      />
    ),
  });

  handleSearch = (selectedKeys, confirm) => {
    confirm();
    this.setState({ searchText: selectedKeys[0] });
    console.log(selectedKeys);
  };

  handleReset = clearFilters => {
    clearFilters();
    this.setState({ searchText: '' });
  };
  
  onSubmit = () => {
    console.log('提交')
    console.log(this.state.data);
  };
  render() {
    const data = this.state.data;
    const columns = [
      {
        title: '编号',
        dataIndex: 'index',
        key: 'index',
        width: '10%',
        // ...this.getColumnSearchProps('index'),
      },
      {
        title: '姓名',
        dataIndex: 'name',
        key: 'name',
        width: '10%',
        ...this.getColumnSearchProps('name'),
      },
      {
        title: '学号',
        dataIndex: 'cno',
        key: 'cno',
        width: '20%',
        align: 'center',
        ...this.getColumnSearchProps('cno'),
      },
      {
        title: '性别',
        dataIndex: 'sex',
        key: 'sex',
        align: 'center'
        // ...this.getColumnSearchProps('sex'),
      },
      {
        title: '角色',
        dataIndex: 'role',
        key: 'role',
        align: 'center'
      },
      {
        title: '学校',
        dataIndex: 'school',
        key: 'school',
        align: 'center'
        // ...this.getColumnSearchProps('school'),
      },
      {
        title: '学院',
        dataIndex: 'collage',
        key: 'collage',
        align: 'center'
        // ...this.getColumnSearchProps('collage'),
      },
      {
        title: '专业',
        dataIndex: 'professional',
        key: 'professional',
        align: 'center'
        // ...this.getColumnSearchProps('professional'),
      },
    ];
    return (
      <div>
        <Table columns={columns} dataSource={data} pagination={{ pageSize: 5 }} />
        <Button type="primary" onClick={this.onSubmit} style={{position:"absolute", top: 40, right: 150}}>提交</Button>
      </div>
    )
  }
}

export default teacherQuery;
