import React, { Component } from 'react';
import { Layout, Menu, Breadcrumb, Icon } from 'antd';
// import '../../App.css';
import {browserHistory} from 'react-router-dom';
const { Header, Content, Footer, Sider } = Layout;
const SubMenu = Menu.SubMenu;

class SiderDemo extends Component {
  state = {
    collapsed: false,
  };

  // 点击跳转相应的组件页面
  handleLinkClick = (e) => {
    if (e.key == "1") {
      browserHistory.push('/myStudent');
    }
  }

  onCollapse = collapsed => {
    console.log(collapsed);
    this.setState({ collapsed });
  };

  render() {
    return (
      <Layout style={{ minHeight: '100vh' }}>
        <Sider collapsible collapsed={this.state.collapsed} onCollapse={this.onCollapse}>
          <div className="logo" />
          <Menu theme="dark" defaultSelectedKeys={['1']} mode="inline" onClick={handleLinkClick}>
            <Menu.Item key="1">
              <Icon type="team" />
              <span>我的学生</span>
            </Menu.Item>
            <Menu.Item key="2">
              <Icon type="solution" />
              <span>学生审核</span>
            </Menu.Item>
            <Menu.Item key="3">
              <Icon type="file-text" />
              <span>选题发布</span>
            </Menu.Item>
            <Menu.Item key="4">
              <Icon type="project" />
              <span>毕设管理</span>
            </Menu.Item>
            <SubMenu
              key="sub1"
              title={
                <span>
                  <Icon type="user" />
                  <span>用户管理</span>
                </span>
              }
            >
              <Menu.Item key="5">用户添加</Menu.Item>
              <Menu.Item key="6">老师查询</Menu.Item>
              <Menu.Item key="7">学生查询</Menu.Item>
            </SubMenu>
            <SubMenu
              key="sub2"
              title={
                <span>
                  <Icon type="team" />
                  <span>我的信息</span>
                </span>
              }
            >
              <Menu.Item key="8">个人中心</Menu.Item>
              <Menu.Item key="9">账号管理</Menu.Item>
            </SubMenu>
          </Menu>
        </Sider>
        <Layout>
          <Header style={{ background: '#fff', padding: 0 }} />
          <Content style={{ margin: '0 16px' }}>
            <Breadcrumb style={{ margin: '16px 0' }}>
              <Breadcrumb.Item>User</Breadcrumb.Item>
              <Breadcrumb.Item>Bill</Breadcrumb.Item>
            </Breadcrumb>
            <div style={{ padding: 24, background: '#fff', minHeight: 360 }}>Bill is a cat.</div>
          </Content>
          <Footer style={{ textAlign: 'center' }}>铅笔头管理系统 @2019</Footer>
        </Layout>
      </Layout>
    );
  }
}

export default SiderDemo;
