import React, { Component } from 'react';
// import '../../App.css';
import './logIn.css';
// import QuickLogIn from '../quickLogIn/quickLogin';
// import ForgotLogIn from '../forgotLogIn/forgotLogIn';
import { Form, Icon, Input, Button, Checkbox } from 'antd';
import { Alert } from 'antd';
import ajax from '../../api/ajax';
import qs from 'qs';

class NormalLoginForm extends Component {
  state = {
    isError: false,
    resValue: {}
  }

  handleSubmit = e => {
    const resValue = this.state.resValue;
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log('Received values of form: ', values);
        this.setState({resValue: values})
      }
    });
    console.log(resValue)
    // const reqResult = () => ajax('http://47.94.103.140/gdom/login/cnopwd', qs.stringify({data}), 'post');
    // reqResult().then(res => {
    //   console.log(res);
    //   if (res.data === 'success') {
    //     console.log('login')
    //     // this.props.history.replace({pathname: '/teacherHome', query: {'id': 123, 'name': 'yz'}});
    //   } else {
    //     this.setState({isError: true})
    //   }
    // }).catch(err => {
    //   console.log(err);
    // })
  };

  render() {
    const { getFieldDecorator } = this.props.form;
    const isError = this.state.isError;
    return (
      <div>
        <p className="title">欢迎登陆粉笔头系统</p>
        {/* {isForgot ? <ForgotLogIn /> : ''}
        {isQuick ? <QuickLogIn /> : ''} */}
        <div className="main">
          <Form onSubmit={this.handleSubmit} className="login-form">
            <Form.Item>
              {getFieldDecorator('username', {
                rules: [{ required: true, message: 'Please input your username!' }],
              })(
                <Input
                  prefix={<Icon type="user" style={{ color: 'rgba(0,0,0,.25)' }} />}
                  placeholder="Username"
                />,
              )}
            </Form.Item>
            <Form.Item>
              {getFieldDecorator('password', {
                rules: [{ required: true, message: 'Please input your Password!' }],
              })(
                <Input
                  prefix={<Icon type="lock" style={{ color: 'rgba(0,0,0,.25)' }} />}
                  type="password"
                  placeholder="Password"
                />,
              )}
            </Form.Item>
            <Form.Item>
              {getFieldDecorator('remember', {
                valuePropName: 'checked',
                initialValue: true,
              })(<Checkbox>Remember me</Checkbox>)}
              <a className="login-form-forgot" href="#/forgotLogIn">
                忘记密码
          </a>
              <Button type="primary" htmlType="submit" className="login-form-button">
                Log in
          </Button>
              Or <a href="#/quickLogIn">快速登陆</a>
            </Form.Item>
             {isError ? <Alert
                message="Error"
                description="密码错误，请重新输入"
                type="error"
                showIcon
             /> : null }
          </Form>
        </div>
      </div>
    );
  }
}

const WrappedNormalLoginForm = Form.create({ name: 'normal_login' })(NormalLoginForm);

export default WrappedNormalLoginForm;
