import React, { Component } from 'react'
import { Table, Divider, Tag, Button } from 'antd';
import personalCenter from '../personalCenter/personalCenter';


export class myStudent extends Component {
  state = {
    // 点击展示详情显示模块
    showDetails : false,
    columns: [
      {
        title: '姓名',
        dataIndex: 'name',
        key: 'name',
        align: 'center'
      },
      {
        title: '学号',
        dataIndex: 'number',
        key: 'number',
        align: 'center'
      },
      {
        title: '题目',
        dataIndex: 'item',
        key: 'item',
        align: 'center'
      },
      {
        title: '期中成绩',
        dataIndex: 'midterm',
        key: 'midterm',
        align: 'center'
      },
      {
        title: '期末成绩',
        dataIndex: 'final',
        key: 'final',
        align: 'center'
      },
      {
        title: '详情信息',
        key: 'action',
        align: 'center',
        render: (text, record) => (
          <span>
            <Button type="primary" onClick={this.showItem}>详情</Button>
            {this.state.showDetails ? <div>
              <span>{record.name}</span>
              <span>{record.number}</span>
            </div> : null}
            {/* <a href="javascript:;">{record.name}</a> */}
          </span>
        ),
      },
    ],
    data: [
      {
        key: '1',
        name: 'John',
        number: 16406050305,
        item: '高数',
        midterm: 90,
        final: 90,
        tags: '',
      },
      {
        key: '1',
        name: 'John',
        number: 16406050305,
        item: '高数',
        midterm: 90,
        final: 90,
        tags: '',
      },
      {
        key: '1',
        name: 'John',
        number: 16406050305,
        item: '高数',
        midterm: 90,
        final: 90,
        tags: '',
      },
    ]
  }
  showItem = (text, record) => {
    this.setState({showDetails: true});
  }
  render() {
    const columns = this.state.columns;
    const data = this.state.data;
    return (
      <div>
        <Table columns={columns} dataSource={data} />
      </div>
    )
  }
}

export default myStudent;
