import React, { Component } from 'react';
import './forgotLogIn.css'
import { Input } from 'antd';
import { Button } from 'antd';

class forgotLogIn extends Component {
  state = {
    isNote: true
  }

  getCode = () => {
    this.setState(prevState => ({
      isNote: !prevState.isNote
    }))
    console.log(this.refs.but.props.children, this.state.isNote)
  };

  render() {
    const isNote = this.state.isNote;
    return (
      <div>
        <p className="title">欢迎登陆粉笔头系统</p>
        <div className="main">
          <Input placeholder="请输入手机号..." className="InpNumber" />
          <div className="getCode">
          <Input placeholder="请输入验证码" className="Inp" size={'large'}/>
          <Button type="primary" ref="but" className="btn" size={'large'} onClick={this.getCode}>{ isNote ? '获取短信验证码': '获取语音验证码'}</Button>
          </div>
          <p>*点击按钮切换验证方式</p>
          <Button type="primary" style={{width: "300px"}}>验证</Button>
        </div>
      </div>
    )
  }
}

export default forgotLogIn;
