import React, { Component } from 'react'
import { Button } from 'antd'

export class selectTopic extends Component {

  getSelectValeu = () => {
    alert(this.value)
  }
  render() {
    return (
      <div>
        <div className="select-item">
         <Button size={'large'} value='one' onClick={this.getSelectValeu}>选题一</Button>
         <Button size={'large'} value='two' onClick={this.getSelectValeu}>选题二</Button>
         <Button size={'large'} value='three' onClick={this.getSelectValeu}>选题三</Button>
         <Button size={'large'} value='four' onClick={this.getSelectValeu}>选题四</Button>
         <Button size={'large'} value='five' onClick={this.getSelectValeu}>选题四</Button>
         <Button size={'large'} value='six' onClick={this.getSelectValeu}>选题五</Button>
        </div>
    </div>
    )
  }
}

export default selectTopic;
