import React, { Component } from 'react'
import { Table, Button } from 'antd';
import ajaxParam from '../../api/ajaxParam';
import qs from 'qs';

class studnetCheck extends Component {
  state = {
    columns: [
      {
        title: '编号',
        dataIndex: 'number',
      },
      {
        title: '姓名',
        dataIndex: 'name',
      },
      {
        title: '学号',
        dataIndex: 'cno',
      },
      {
        title: '班级',
        dataIndex: 'class',
      },
      {
        title: '题目',
        dataIndex: 'item',
      },
      {
        title: '优先级',
        dataIndex: 'priority',
      },
    ],
    data: [
      {
        key: 0,
        number: '0',
        name: '杨卓',
        cno: '16406050305',
        class: '自动化1603班',
        item: '基于ARM实现的智能小车',
        priority: 'A'
      }
    ],
    selectedRowKeys: [], // Check here to configure the default column
    loading: false,
    counter: 1,
  };

  componentDidMount = () => {
    const { uid, dataKey } = this.state;
    const data = { "uid": 2 };
    const reqResult = (data) => ajaxParam('http://47.94.103.140/gdom/lecture/getselecting', qs.stringify(data), 'post');
    reqResult(data).then(res => {
      console.log(res);
      const res_data = res.data;
      const table_data = [];
      res_data.forEach(item => {
        let data_item = {
          key: item["student"].sid,
          name: item["userInfo"].username,
          number: item["student"].sno,
          item: item["tittle"],
          midterm: item["student"].midScore ? item["student"].midScore : '—',
          final: item["student"].finalScore ? item["student"].finalScore : '—',
          score: item["student"].score ? item["student"].score : '—',
        }
        table_data.push(data_item)
      });
      // console.log(table_data);
      // this.setState({
      //   data: table_data
      // })
    }).catch(err => {
      console.log(err);
    })
  }


  start = () => {
    this.setState({ loading: true });
    this.setState({ counter: this.state.counter + 1 });
    // ajax request after empty completing
    setTimeout(() => {
      this.setState({
        selectedRowKeys: [],
        loading: false,
      });
    }, 1000);
  };

  onSelectChange = selectedRowKeys => {
    console.log('selectedRowKeys changed: ', selectedRowKeys);
    this.setState({ selectedRowKeys });
  };

  // 点击拿到每一行的数据
  onClickSelect = (recordr) => {
    console.log(recordr);
  }

  render() {
    const { loading, selectedRowKeys, columns, data, counter } = this.state;
    const rowSelection = {
      selectedRowKeys,
      onChange: this.onSelectChange,
      onSelect: this.onClickSelect,
      type: 'radio'
    };
    const hasSelected = selectedRowKeys.length > 0 && counter <= 4;
    return (
      <div>
        <div>
          <Button type="primary" onClick={this.start} disabled={!hasSelected} loading={loading}>
            提交
          </Button>
        </div>
        <Table rowSelection={rowSelection} columns={columns} dataSource={data} pagination={{ pageSize: 4 }} />
        {/* <Pagination defaultCurrent={1} total={50} /> */}
      </div>
    );
  }
}

export default studnetCheck
