import React, { Component } from 'react'
import { Table, Button } from 'antd';
// const data = [];
// for (let i = 0; i < 46; i++) {
//   data.push({
//     key: i,
//     name: `Edward King ${i}`,
//     age: 32,
//     address: `London, Park Lane no. ${i}`,
//   });
// }

class studnetCheck extends Component {
  state = {
    columns: [
      {
        title: '编号',
        dataIndex: 'number',
      },
      {
        title: '姓名',
        dataIndex: 'name',
      },
      {
        title: '学号',
        dataIndex: 'cno',
      },
      {
        title: '班级',
        dataIndex: 'class',
      },
      {
        title: '题目',
        dataIndex: 'item',
      },
      {
        title: '优先级',
        dataIndex: 'priority',
      },
    ],
    data: [
      {
        key: 0,
        number: '0',
        name: '杨卓',
        cno: '16406050305',
        class: '自动化1603班',
        item: '基于ARM实现的智能小车',
        priority: 'A'
      }, {
        key: 1,
        number: '1',
        name: '杨卓',
        cno: '16406050305',
        class: '自动化1603班',
        item: '基于ARM实现的智能小车',
        priority: 'A'
      }, {
        key: 2,
        number: '2',
        name: '杨卓',
        cno: '16406050305',
        class: '自动化1603班',
        item: '基于ARM实现的智能小车',
        priority: 'B'
      }, {
        key: 3,
        number: '3',
        name: '杨卓',
        cno: '16406050305',
        class: '自动化1603班',
        item: '基于ARM实现的智能小车',
        priority: 'C'
      },
      {
        key: 4,
        number: '4',
        name: '杨卓',
        cno: '16406050305',
        class: '自动化1603班',
        item: '基于ARM实现的智能小车',
        priority: 'C'
      },{
        key: 5,
        number: '5',
        name: '杨卓',
        cno: '16406050305',
        class: '自动化1603班',
        item: '基于ARM实现的智能小车',
        priority: 'C'
      },{
        key: 6,
        number: '6',
        name: '杨卓',
        cno: '16406050305',
        class: '自动化1603班',
        item: '基于ARM实现的智能小车',
        priority: 'C'
      }
    ],
    selectedRowKeys: [], // Check here to configure the default column
    loading: false,
  };

  start = () => {
    this.setState({loading: true})
    // ajax request after empty completing
    setTimeout(() => {
      this.setState({
        selectedRowKeys: [],
        loading: false,
      });
    }, 1000);
  };

  onSelectChange = selectedRowKeys => {
    console.log('selectedRowKeys changed: ', selectedRowKeys);
    this.setState({ selectedRowKeys });
  };

  // 点击拿到每一行的数据
  onClickSelect = (recordr) => {
    console.log(recordr);
  }

  render() {
    const { loading, selectedRowKeys, columns, data } = this.state;
    const rowSelection = {
      selectedRowKeys,
      onChange: this.onSelectChange,
      onSelect: this.onClickSelect,
      type: 'radio'
    };
    const hasSelected = selectedRowKeys.length > 0;
    return (
      <div>
        <div style={{ marginBottom: 16 }}>
          <Button type="primary" onClick={this.start} disabled={!hasSelected} loading={loading}>
            提交
          </Button>
        </div>
        <Table rowSelection={rowSelection} columns={columns} dataSource={data} />
      </div>
    );
  }
}

export default studnetCheck
