import React, { Component } from 'react'
import { Table, Divider, Tag ,Button} from 'antd';
import personalCenter from '../personalCenter/personalCenter';

const columns = [
  {
    title: '姓名',
    dataIndex: 'name',
    key: 'name',
  },
  {
    title: '学号',
    dataIndex: 'number',
    key: 'number',
  },
  {
    title: '题目',
    dataIndex: 'item',
    key: 'item',
  },
  {
    title: '期中成绩',
    dataIndex: 'midterm',
    key: 'midterm',
    align: 'center'
  },
  {
    title: '期末成绩',
    dataIndex: 'final',
    key: 'final',
    align: 'center'
  },
  {
    title: 'Action',
    key: 'action',
    render: (text, record) => (
      <span>
        <Button type="primary">详情</Button>
        {/* <a href="javascript:;">Invite {record.name}</a> */}
        {/* <Divider type="vertical" /> */}
        {/* <a href="javascript:;">Delete</a> */}
      </span>
    ),
},  
];

const data = [
  {
    key: '1',
    name: 'John',
    number: 16406050305,
    item: '高数',
    midterm: 90,
    final: 90 ,
    tags: '',
  },
  {
    key: '1',
    name: 'John',
    number: 16406050305,
    item: '高数',
    midterm: 90,
    final: 90 ,
    tags: '',
  },
  {
    key: '1',
    name: 'John',
    number: 16406050305,
    item: '高数',
    midterm: 90,
    final: 90 ,
    tags: '',
  },
];

export class myStudent extends Component {

  showItem = () => {
    alert(123);
  }
  render() {
    return (
      <div>
        <Table columns={columns} dataSource={data} />
      </div>
    )
  }
}

export default myStudent;
